properties.jslt-transform-transformation:
let dt = parse-time(.date, "yyyy-MM-dd HH:mm:ss")
let dts = split($dt + "", "E")
let fff = split(.date, ",")[1]
let ts = $dts[0][0] + $dts[0][2 : 11] + $fff + "000000"
let nano = split($ts + "", ".")[1]
let dt_str = format-time($dt, "yyyy-MM-dd HH:mm:ss")

{
  "streams": [
    {
      "stream": {
        "Level" : .Level,
        "thread" : .thread,
        "logger" : .logger,
      },
      "values": [
          [ $ts, .message ]
      ]
    }
  ]
}
